package com.example.controller;import com.example.entity.Info;import com.example.mapper.InfoMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.servlet.http.HttpServletResponse;import java.sql.Blob;/** * @Auther: Zhou_Li * @Date: 2021/7/13 17:03 * @Description: */@RestController@RequestMapping("/testBoot")public class InfoController {    @Autowired    private InfoMapper infoMapper;    @RequestMapping("getTextById/{id}")    public String getTextById(@PathVariable int id){        return infoMapper.getClobById(id);    }    @RequestMapping("getAllIcon")    public Object[] getAllBlob(){        Info[] infos = infoMapper.getAllBlob();        Object[] objects = new Object[infos.length];        int i = 0;        for(Info info : infos){            objects[i] = infos[i].getIcon();            i++;        }        return objects;    }    @RequestMapping("getIconById/{id}")    public Object getBlobById(@PathVariable int id, HttpServletResponse response){        Object object = infoMapper.getBlobById(id).getIcon();        return infoMapper.getBlobById(id).getIcon();    }}