package com.example.service;import com.example.entity.Doctor;import com.example.mapper.DoctorMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @Auther: Zhou_Li * @Date: 2021/7/8 10:59 * @Description: */@Servicepublic class DoctorService {    @Autowired    DoctorMapper doctorMapper;    public Doctor selById(int id){        return doctorMapper.selById(id);    }    public Doctor[] selByName(String name){        return doctorMapper.selByName(name);    }    public Doctor[] findAllDoctor(){        return doctorMapper.findAllDoctor();    }}