package com.example.service;import com.example.entity.Info;import com.example.mapper.InfoMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.sql.Blob;/** * @Auther: Zhou_Li * @Date: 2021/7/13 17:01 * @Description: */@Servicepublic class InfoService {    @Autowired    private InfoMapper infoMapper;    public Info getBlobById(int id){        return infoMapper.getBlobById(id);    }    public String getClobById(int id){        return infoMapper.getClobById(id);    }    public Info[] getAllBlob(){        return infoMapper.getAllBlob();    }}